            videoControls.style.bottom = '-54px';
        });
        // 视频是否加载完成
        videoContent.addEventListener('play', function () {
            videoPlay.className = 'iconfont icon-24gf-pause2';
            timer = setInterval(function () {
                playing();
            }, 1000);
        });
        // 视频暂停
        videoContent.addEventListener('pause', function () {
            videoPlay.className = 'iconfont icon-bofang';
            clearInterval(timer);
        });
        videoPlay.addEventListener('click', function () {
            if (videoContent.paused) {
                videoContent.play();
            }
            else {
                videoContent.pause();
            }
        });
        // 播放中进度条变化
        function playing() {
            videoTimes[1].innerHTML = formatTime(videoContent.duration);
            var scale = videoContent.currentTime / videoContent.duration;
            var scaleSuc = videoContent.buffered.end(0) / videoContent.duration;
            videoTimes[0].innerHTML = formatTime(videoContent.currentTime);
            videoProgress[0].style.width = scale * 100 + '%';
            videoProgress[1].style.width = scaleSuc * 100 + '%';
            videoProgress[2].style.left = scale * 100 + '%';
        }
        // 时间格式化
        function formatTime(number) {
