        videoProgress[2].addEventListener('mousedown', function (ev) {
            var _this = this;
            var downX = ev.pageX;
            var downL = this.offsetLeft;
            document.onmousemove = function (ev) {
                var scale = (ev.pageX - downX + downL + 8) / _this.parentNode.offsetWidth;
                if (scale < 0) {
                    scale = 0;
                }
                else if (scale > 1) {
                    scale = 1;
                }
                videoProgress[0].style.width = scale * 100 + '%';
                videoProgress[1].style.width = scale * 100 + '%';
                _this.style.left = scale * 100 + '%';
                videoContent.currentTime = scale * videoContent.duration;
            };
            document.onmouseup = function () {
                document.onmousemove = document.onmouseup = null;
            };
            ev.preventDefault();
        });
